package com.graphcoloring.main;

// TODO: Auto-generated Javadoc
/**
 * The Class Camera.
 */
public class Camera {

	/** The y. */
	private float x, y;
	
	/**
	 * Instantiates a new camera.
	 *
	 * @param x the x
	 * @param y the y
	 */
	public Camera(float x, float y) {
		this.x = x;
		this.y = y;
	}
	
	/**
	 * Sets the x.
	 *
	 * @param x the new x
	 */
	public void setX(float x) {
		this.x = x;
	}
	
	/**
	 * Sets the y.
	 *
	 * @param y the new y
	 */
	public void setY(float y) {
		this.y = y;
	}
	
	/**
	 * Gets the x.
	 *
	 * @return the x
	 */
	public float getX() {
		return x;
	}
	
	/**
	 * Gets the y.
	 *
	 * @return the y
	 */
	public float getY() {
		return y;
	}
}
